DROP TABLE IF EXISTS `tb_investor_project`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `tb_investor_project` (
  `id` varchar(45) COLLATE utf8_unicode_ci NOT NULL,
  `investor_id` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '投资者id',
  `project_id` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目id',
  `amount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '投资金额',
  `status` enum('0','1','2','3') COLLATE utf8_unicode_ci NOT NULL DEFAULT '0' COMMENT '购买状态(0:未付款,1:付款,2:退款,3:结算)',
  `payment_no` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '支付交易流水号',
  `payment_way` enum('0','11','20','30') COLLATE utf8_unicode_ci NOT NULL DEFAULT '0' COMMENT '支付方式\n0=全部支付（账户余额+银行卡）\n11=账户余额支付\n20=银行卡支付\n30=快捷支付\n商户不传默认0或不传',
  `payment_platform` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0' COMMENT '支付平台(0:PC端,1:移动端)',
  `investment_type` enum('10','20') COLLATE utf8_unicode_ci NOT NULL DEFAULT '10' COMMENT '投资类型\n10=项目直投(默认)\n20=债权转让',
  `payment_date` datetime DEFAULT NULL COMMENT '成功支付时间',
  `refund_date` datetime DEFAULT NULL COMMENT '退款时间',
  `settlement_date` datetime DEFAULT NULL COMMENT '结算时间',
  `interest` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '项目结算后获得的利息',
  `branch_id` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '网点ID',
  `manager_id` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '客户经理ID',
  `create_time` datetime NOT NULL,
  `update_time` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `tb_investor_project_project_fk_idx` (`project_id`),
  KEY `tb_investor_project_investor_fk_idx` (`investor_id`),
  KEY `tb_investor_project_branch_fk_idx` (`branch_id`),
  KEY `tb_investor_project_manager_fk_idx` (`manager_id`),
  CONSTRAINT `tb_investor_project_branch_fk` FOREIGN KEY (`branch_id`) REFERENCES `tb_bank_branch` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `tb_investor_project_manager_fk` FOREIGN KEY (`manager_id`) REFERENCES `tb_bank_branch_manager` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `tb_investor_project_investor_fk` FOREIGN KEY (`investor_id`) REFERENCES `tb_investor` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `tb_investor_project_project_fk` FOREIGN KEY (`project_id`) REFERENCES `tb_project` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='投资者投资的项目';
/*!40101 SET character_set_client = @saved_cs_client */;

