DROP TABLE IF EXISTS `tb_project`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `tb_project` (
  `id` varchar(45) COLLATE utf8_unicode_ci NOT NULL,
  `name` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目名称',
  `borrower_id` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目的借款人',
  `number` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目编号(唯一性)',
  `type_id` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目类型',
  `increase_type_id` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目收益方式id',
  `origin_agency` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目来源机构',
  `amount` decimal(10,2) NOT NULL COMMENT '融资项目发行金额',
  `yield_rate` decimal(8,6) NOT NULL COMMENT '融资项目预期投资收益率',
  `borrow_rate` decimal(8,6) NOT NULL COMMENT '借款年利率',
  `min_invest_amount` decimal(10,2) NOT NULL COMMENT '融资项目的最小投资金额',
  `sell_limit` int(8) NOT NULL COMMENT '融资项目的最大购买数',
  `vip` enum('0','1') COLLATE utf8_unicode_ci NOT NULL DEFAULT '0' COMMENT '是否是VIP项目\n0:不是,1:是',
  `yield_days` tinyint(2) NOT NULL COMMENT '融资项目投标结束后第几天后开始计息',
  `loan_period` int(8) NOT NULL COMMENT '融资项目借款期限',
  `presale_date_start` datetime NOT NULL COMMENT '融资项目预售开始时间',
  `presale_date_end` datetime NOT NULL COMMENT '融资项目预售结束时间',
  `tender_date_start` datetime NOT NULL COMMENT '融资项目投标开始时间',
  `tender_date_end` datetime NOT NULL COMMENT '融资项目投标结束时间',
  `status` enum('0','1','11','2','3','31','4','5','6','7','8','99') COLLATE utf8_unicode_ci NOT NULL COMMENT '融资项目的状态(0:项目创建完毕,待审核发布,1:审核发布中,11:审核发布不通过,待审核,2:审核发布通过,根据项目预售时间和招标时间判断[预售前，预售中，招标中，待审核放款],3:审核放款中,31:审核放款不通过,4:退款,项目关闭,5:审核放款通过,待放款6:已放款,等待还款,7:已还款,等待结算,8:已结算，项目关闭,99:项目已删除)',
  `project_url` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '项目链接',
  `refund_date` datetime DEFAULT NULL COMMENT '退款时间',
  `loan_date` datetime DEFAULT NULL COMMENT '放款时间',
  `repayment_date_expected` datetime NOT NULL COMMENT '预期还款日期',
  `repayment_date` datetime DEFAULT NULL COMMENT '实际还款时间',
  `settlement_date` datetime DEFAULT NULL COMMENT '结算时间',
  `create_time` datetime NOT NULL,
  `update_time` datetime NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `number_UNIQUE` (`number`),
  KEY `type_fk_idx` (`type_id`),
  KEY `increase_type_fk_idx` (`increase_type_id`),
  KEY `tb_project_ borrower_fk_idx` (`borrower_id`),
  CONSTRAINT `tb_project_?borrower_fk` FOREIGN KEY (`borrower_id`) REFERENCES `tb_borrower` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `tb_project_increase_type_fk` FOREIGN KEY (`increase_type_id`) REFERENCES `tb_increase_type` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `tb_project_type_fk` FOREIGN KEY (`type_id`) REFERENCES `tb_project_type` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='融资项目';
